import { useState } from 'react';
import axios from 'axios';
import './App.css'

function Transactions() {

    return (
        <div class="body">
          <div class="search-bar">
            <input
              type="text"
              placeholder="Enter Block Number"
              value={blockNumber.num}
              onChange={handle}
              class="search-box"
              onKeyDown={getRes}
            />
            </div>
            <div class="block">
                <div class="block-info">
                  <p>Block Number: </p>
                  <p>Timestamp: </p>
                  <p>Transactions: </p>
                  <p>Gas Used: </p>
                  <p>Gas Limit: </p>
                  <p>Base Fee Per Gas: </p>
                  <p>Burnt Fees: </p>
                  <p>Extra Data: </p>
                  <p>Hash: </p>
                  <p>Parent Hash: </p>
                  <p>Nonce: </p>
                </div>
                <div class="block-values">
                  {number && <p>{number}</p>}
                  {timestamp && <p>ðŸ•“{timestamp}</p>}
                  {transactions && <p><a href="" class="tran">{transactions} transactions</a></p>}
                  {miner && <p>{miner}</p>}
                    {hash && <p>{hash}</p>}
                    {parentHash && <p>{parentHash}</p>}
                    {nonce && <p>{nonce}</p>}
                    
                    {difficulty && <p>{difficulty}</p>}
                    {extraData && <p>{extraData}</p>}
                </div>
          </div>
        </div>
      );
    

}
