import { useState } from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Route, Link, Routes,NavLink } from 'react-router-dom';
import './App.css'; 
// import Transactions from './Transactions';
import Transactions from './Transactions';



function App() {
  const [blockNumber, setBlockNumber] = useState({ num: '' });
  const [hash, setHash] = useState('');
  const [parentHash, setParenthash] = useState('');
  const [nonce, setNonce] = useState('');
  const [timestamp, setTimestamp] = useState('');
  const [difficulty, setDifficulty] = useState('');
  const [number, setNumber] = useState('');
  //const [gasUsed, setgasUsed] = useState('');
  const [miner, setMiner] = useState('');
  const [extraData, setExtradata] = useState('');
  const [transactions, setTransactions] = useState('');
  //const [baseFee, setBasefee] = useState('');

  function handle(e){
    setBlockNumber({ num: e.target.value });
  }


  async function getRes(event){
    if (event.key === 'Enter') {
    try{
      const res = await axios.post('http://localhost:3001/gift', {
        num: blockNumber.num
      })
      const r = res.data;
      setHash(r.hash);
      setParenthash(r.parentHash);
      setNonce(r.nonce);
      setNumber(r.number);
      setDifficulty(r.difficulty);
      setExtradata(r.extraData)
      setMiner(r.miner);
      setTransactions(r.transactions.length);
      //setBasefee(r.baseFeePerGas);
      setTimestamp(formatTimestamp(r.timestamp));

      function formatTimestamp(transactionTime) {
        const currentTime = Math.floor(Date.now() / 1000); // Current time in seconds  
        const timeDifference = currentTime - transactionTime;
        const hours = Math.floor(timeDifference / 3600);
        const minutes = Math.floor((timeDifference % 3600) / 60);
        const formattedTimeDifference =
          (hours > 0 ? hours + ' hr ' : '') + (minutes > 0 ? minutes + ' mins ' : '') + 'ago';
        const transactionTimestamp = new Date(transactionTime * 1000).toLocaleString('en-US', {
          year: 'numeric',
          month: 'short',
          day: '2-digit',
          hour: '2-digit',
          minute: '2-digit',
          second: '2-digit',
          timeZoneName: 'short',
        });
        return `${formattedTimeDifference} (${transactionTimestamp} +UTC)`;
      }


    }
    catch(e){
      console.log(e);
    }
  }
  }


  return (
    <Router>
    <div class="body">
      <div class="search-bar">
        <input
          type="text"
          placeholder="Enter Block Number"
          value={blockNumber.num}
          onChange={handle}
          class="search-box"
          onKeyDown={getRes}
        />
        </div>
        <div class="block">
            <div class="block-info">
              <p>Block Number: </p>
              <p>Timestamp: </p>
              <p>Transactions: </p>
              <p>Gas Used: </p>
              <p>Gas Limit: </p>
              <p>Base Fee Per Gas: </p>
              <p>Burnt Fees: </p>
              <p>Extra Data: </p>
              <p>Hash: </p>
              <p>Parent Hash: </p>
              <p>Nonce: </p>
            </div>
            <div class="block-values">
              {number && <p>{number}</p>}
              {timestamp && <p>🕓{timestamp}</p>}
              {transactions && <p> 
                <NavLink to="/" activeClassName="active">{transactions} transactions</NavLink></p>}
              {miner && <p>{miner}</p>}
                {hash && <p>{hash}</p>}
                {parentHash && <p>{parentHash}</p>}
                {nonce && <p>{nonce}</p>}

                {difficulty && <p>{difficulty}</p>}
                {extraData && <p>{extraData}</p>}
            </div>
      </div>
      <Routes>
      <Route path="/" component={<Transactions/>} />
      </Routes>
    </div>
    </Router>
  );

}

export default App;
