import * as React from 'react';
import Container from 'react-bootstrap/esm/Container';
import Card from 'react-bootstrap/Card';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import { useState } from 'react';
import {Link} from "react-router-dom";
import './App.css'

const { Alchemy, Network, Utils } = require("alchemy-sdk");


const setting = {
  apiKey: "1dFCMwz6qW-UR-dDdjd5km0vL3aKzjUN",
  Network: Network.ETH_MAINNET,
};


const provider = new Alchemy(setting);


export default function Tran() {
  const myStyle = {
    backgroundImage: `url(/back1.jpg)`,
    backgroundSize: 'cover',
    backgroundRepeat: 'no-repeat',}

    const [showSecondDiv, setShowSecondDiv] = useState(false);
    const [to, setTo] = useState('');
    const [from, setFrom] = useState('');
    const [contractAddress, setContractAddress] = useState('');
    const [transactionIndex, setTransactionIndex] = useState('');
    const [root, setRoot] = useState(''); // Assuming root is a string
    const [logsBloom, setLogsBloom] = useState('');
    const [transactionHash, setTransactionHash] = useState('');
    const [logs, setLogs] = useState([]); // Assuming logs is an array
    const [blockNumber, setBlockNumber] = useState('');
    const [type, setType] = useState('');
    const [status, setStatus] = useState('');
    const [cummulativeGasUsed, setCummulativeGasUsed] = useState('');
    const [effectiveGasPrice, setEffectiveGasPrice] = useState('');
    const [transactions,setTransactions] = useState([]);
    

    const handleButtonClick = (x) => {
      let i =0;
      setShowSecondDiv(true);
      if(i<1){
      gettransactionReceipt(x);   
      i++;} 
    };

    getTransactions();
    async function getTransactions(){
      let tra = [];
      const r = await provider.core.getBlockWithTransactions(18228654);
      for(let i=0;i<r.transactions.length;i++){
      tra.push(r.transactions[i].hash);
    }
      
      setTransactions(tra);

    }

    async function gettransactionReceipt(x){
      const r = await provider.core.getTransactionReceipt(x);
      console.log(r);
      setFrom(r.from);
      setBlockNumber(r.blockNumber);
      setContractAddress(r.contractAddress);
      setCummulativeGasUsed(parseInt(r.cumulativeGasUsed._hex, 16))
      setEffectiveGasPrice(parseInt(r.effectiveGasPrice._hex, 16))
      setLogs(r.logs.length);
      setLogsBloom(r.logsBloom);
      setStatus(r.status);
      setTransactionHash(r.transactionHash);
      setTransactionIndex(r.transactionIndex);
      setType(r.type);
    }
  
    const renderSecondDiv = () => {
      if (showSecondDiv) {
        return (
          <div class="block-info">
            <div class="block-label">
            <p>From: </p>
            <p>To: </p>
            <p>Contract Address: </p>
            <p>Transaction Index: </p>
            <p>Root: </p>
            <p>Gas Used: </p>
            <p>Logs Bloom: </p>
            <p>Transaction Hash: </p>
            <p>Logs: </p>
            <p>Block Number: </p>
            <p>Type: </p>
            <p>Status: </p>
            <p>Cumulative Gas Used: </p>
            <p>Effective Gas Price: </p>
            </div>
            <div className="block-values">
              {from && <p>{from}</p>}
              {t && <p style={{fontSize: '17px'}}>ðŸ•“{t}</p>}
              {transactions && (
                <p>
                  <Link to="/transactions" style={{textDecoration: 'none',color: 'rgb(0, 110, 255)',backgroundColor:'black'}}>{transactions} transactions</Link>
                </p>
              )}
              {miner && <p>{miner}</p>}
              {gasUsed && <p>{gasUsed}</p>}
              {gasLimit && <p>{gasLimit}</p>}
              {baseFeepergas && <p>{baseFeepergas} ETH</p>}
              {extraData && <p>{extraData}</p>}
              {nonce && <p>{nonce}</p>}
              {hash && <p style={{fontSize: '12.3px',color:'yellow',fontWeight: 'bold',paddingTop: '3px'}}>{hash}</p>}
              {parentHash && <p style={{fontSize: '12.3px',color:'yellow',fontWeight: 'bold',paddingTop: '9px'}}>{parentHash}</p>}
            </div>
          </div>
        );
      }
      return null;
    };


  return (
    <div style={myStyle}>
    <Container>
  <Card style={{ height: '4rem', border: 'none', background: 'transparent'}}></Card>
        <Row>
        <Col>
            <Card border="dark" style={{ width: '38rem',height: '40rem',background: 'black',opacity: 0.9,color: 'white',border: '1px solid white'}}>
            <Card.Header style={{fontWeight: 'bold'}}>Transactions</Card.Header>
            <Card.Body>
            <div class="card1">
          {transactions.map((hash, index) => (
            <div key={index}>
              <button class = "btn1" onClick={() => handleButtonClick(hash)} id="btnval">{hash}</button>
            <hr className="hash-divider" />   
            </div>         
          ))}
            </div>
            {/* <div className={Style.container__block}>
            {transaction.map((el, i) => (
              <div key={i + 1} className={Style.oneBlock}>
                <div className={Style.info}>
                  <p></p>
                  <div style={{display: 'flex',gap: '2rem'}}>
                    <img src={process.env.PUBLIC_URL + '/logo2.png'} style={{height: '40px', width:'40px'}}></img>            
                    <button class = "btn1" onClick={() => handleButtonClick({el})} id="btnval">{el}</button><br/><p></p>
                    <p style={{paddingTop: '2px',color: 'gray'}}>{sec[el]}</p><br/><p></p>
                  </div>
                </div>
              </div>))}
              </div> */}
            </Card.Body>
            </Card>
        </Col>
            <Col>
                <Card border="dark" style={{ width: '40rem',height: '40rem',background: 'black',opacity: 0.9,border: '1px solid white',color:'white' }}>
                <Card.Header style={{fontWeight: 'bold'}}>Transaction Details</Card.Header>
                <Card.Body style={{justifyContent: 'space-evenly'}}>
                {/* {renderSecondDiv()} */}
                </Card.Body>
                </Card>
        </Col>
      </Row>
      </Container>
      <Card style={{ height: '5.7rem', border: 'none', background: 'transparent'}}></Card>
    </div>
  );
}
