import { useState } from 'react';
import axios from 'axios';
import './App.css'; 



function App() {
  const [blockNumber, setBlockNumber] = useState({ num: '' });
  const [hash, setHash] = useState('');
  const [parentHash, setParenthash] = useState('');
  const [nonce, setNonce] = useState('');
  const [timestamp, setTimestamp] = useState('');
  const [difficulty, setDifficulty] = useState('');
  const [number, setNumber] = useState('');
  //const [gasUsed, setgasUsed] = useState('');
  const [miner, setMiner] = useState('');
  const [extraData, setExtradata] = useState('');
  //const [transactions, setTransactions] = useState('');
  //const [baseFee, setBasefee] = useState('');

  function handle(e){
    setBlockNumber({ num: e.target.value });
  }


  async function getRes(event){
    if (event.key === 'Enter') {
    try{
      const res = await axios.post('http://localhost:3001/gift', {
        num: blockNumber.num
      })
      const r = res.data;
      setHash(r.hash);
      setParenthash(r.parentHash);
      setTimestamp(new Date(r.timestamp * 1000).toUTCString());
      setNonce(r.nonce);
      setNumber(r.number);
      setDifficulty(r.difficulty);
      setExtradata(r.extraData)
      setMiner(r.miner);
      setTransactions(r.transactions.length);
      //setBasefee(r.baseFeePerGas);
    }
    catch(e){
      console.log(e);
    }
  }
  }


  return (
    <div class="body">
      <div class="search-bar">
        <input
          type="text"
          placeholder="Enter Block Number"
          value={blockNumber.num}
          onChange={handle}
          class="search-box"
          onKeyDown={getRes}
        />
        </div>
        <div class="block">
            <div class="block-info">
              <p>Block Number: </p>
              <p>Timestamp: </p>
              <p>Transactions: </p>
              <p>Gas Used: </p>
              <p>Gas Limit: </p>
              <p>Base Fee Per Gas: </p>
              <p>Burnt Fees: </p>
              <p>Extra Data: </p>
              <p>Hash: </p>
              <p>Parent Hash: </p>
              <p>Nonce: </p>
              </div>
              {number && <p>{number}</p>}
              {timestamp && <p>{timestamp}</p>}
              {transactions && <p>{transactions}</p>}


                {hash && <p>Hash:{hash}</p>}
                {parentHash && <p>ParentHash:{parentHash}</p>}
                {nonce && <p>Nonce:{nonce}</p>}

                {difficulty && <p>Difficulty:{difficulty}</p>}
                {extraData && <p>extraData:{extraData}</p>}
      </div>
    </div>
  );

}

export default App;
