//import { Network } from 'alchemy-sdk';
import { useState } from 'react';
import axios from 'axios';




function App() {
  const [blockNumber, setBlockNumber] = useState();
  const [giftMessage, setGiftMessage] = useState('');

  async function getRes(){
    try{
      const res = await axios.post('http://localhost:3001/gift', {
        num: blockNumber
      })
      const r = res.data;
      setGiftMessage(r);
    }
    catch(e){
      console.log(e);
    }
  }

  return (
    <div className="App">
      <h1>Check Block</h1>
      <div className="centered-form">
        <form>
          <input
            type="text"
            placeholder="Enter Block Number"
            value={setBlockNumber(blockNumber)}
          />
          <button type="button" onClick={getRes}>
            Click Me!!
          </button>
        </form>
        {giftMessage && <p>{giftMessage}</p>}
      </div>
    </div>
  );

}

export default App;
