import { useState } from 'react';
import axios from 'axios';
import './App.css';

import { useNavigate } from 'react-router-dom';

function Home() {
  const [blockNumber, setBlockNumber] = useState({ num: '' });
  const [hash, setHash] = useState('');
  const [parentHash, setParenthash] = useState('');
  const [nonce, setNonce] = useState('');
  const [miner,setMiner] = useState('');
  const [timestamp, setTimestamp] = useState('');
  const [difficulty, setDifficulty] = useState('');
  const [number, setNumber] = useState('');
  const [extraData, setExtradata] = useState('');
  const [transactions, setTransactions] = useState('');

  function handle(e) {
    setBlockNumber({ num: e.target.value });
  }

  const navigate = useNavigate();

  const navigateToContacts = () => {
    // ðŸ‘‡ï¸ navigate to /contacts
    navigate('./Transactions');
  }; // Replace '/' with the path to the next page


  async function getRes(event) {
    if (event.key === 'Enter') {
      try {
        const res = await axios.post('http://localhost:3001/gift', {
          num: blockNumber.num,
        });
        const r = res.data;
        setHash(r.hash);
        setParenthash(r.parentHash);
        setNonce(r.nonce);
        setNumber(r.number);
        setDifficulty(r.difficulty);
        setExtradata(r.extraData);
        setMiner(r.miner);
        setTransactions(r.transactions.length);
        setTimestamp(formatTimestamp(r.timestamp));

        function formatTimestamp(transactionTime) {
          const currentTime = Math.floor(Date.now() / 1000); // Current time in seconds
          const timeDifference = currentTime - transactionTime;
          const hours = Math.floor(timeDifference / 3600);
          const minutes = Math.floor((timeDifference % 3600) / 60);
          const formattedTimeDifference =
            (hours > 0 ? hours + ' hr ' : '') +
            (minutes > 0 ? minutes + ' mins ' : '') +
            'ago';
          const transactionTimestamp = new Date(
            transactionTime * 1000
          ).toLocaleString('en-US', {
            year: 'numeric',
            month: 'short',
            day: '2-digit',
            hour: '2-digit',
            minute: '2-digit',
            second: '2-digit',
            timeZoneName: 'short',
          });
          return `${formattedTimeDifference} (${transactionTimestamp} +UTC)`;
        }
      } catch (e) {
        console.log(e);
      }
    }
  }

  return (
    <>

    </>
  );
}

export default Home;
