import 'bootstrap/dist/css/bootstrap.min.css';
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import Card from 'react-bootstrap/Card';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import ListGroup from 'react-bootstrap/ListGroup';
import NavDropdown from 'react-bootstrap/NavDropdown';
import { useState,useEffect,useContext } from 'react';
import axios from 'axios';
import Style from "./index.module.css";
import { EtherProvider} from './Etherscan';
import {Link} from "react-router-dom";

function Navigation() {
    const myStyle = {
      backgroundImage: `url(/back.jpg)`,
      backgroundSize: 'cover',
      opacity: 1.4,
      backgroundRepeat: 'no-repeat',}

      const {transaction} = useContext(EtherProvider);
      const [ethPrice,setEthprice] = useState('');
      const [ethBtc,setEthbtc] = useState('');
      const [totalNodes,setTotalnodes] = useState('');

      console.log(transaction);

      

    useEffect(() => {
      async function getBlockNumber() {
        const response = await axios.get(
          'https://api.etherscan.io/api?module=stats&action=ethprice&apikey=53YRRR2ZWJ1D14S2AU85A8M3IJXX9EKH48'
        );
        const res = await axios.get(
          'https://api.etherscan.io/api?module=stats&action=nodecount&apikey=53YRRR2ZWJ1D14S2AU85A8M3IJXX9EKH48')
        var ethPrice = response.data.result.ethusd;
        const ethBtc = response.data.result.ethbtc;
        const totalNodes = res.data.result.TotalNodeCount;

        const firstCharacter = ethPrice[0];
        const restOfString = ethPrice.slice(1);
        const stringWithComma = firstCharacter + ',' + restOfString;
        setEthprice(stringWithComma); 
        setEthbtc(ethBtc);
        setTotalnodes(totalNodes);
      }
  
      getBlockNumber();
    });
    

  return (
    <div style={myStyle}>
    <Navbar expand="lg" className="bg-body-tertiary" bg="dark" data-bs-theme="dark">
      <Container fluid>
        <Navbar.Brand href="#">Hexplorer</Navbar.Brand>
          <Nav
            className="me-auto my-2 my-lg-0"
            style={{ maxHeight: '100px' }}
            navbarScroll
          >
            <Nav.Link href="#action1">Home</Nav.Link>
            <NavDropdown title="Network" id="navbarScrollingDropdown">
              <NavDropdown.Item href="#action3">Ethereum Mainnet</NavDropdown.Item>
              <NavDropdown.Divider />
              <NavDropdown.Item href="#action5">
                Sepolia Testnet
              </NavDropdown.Item>
              <NavDropdown.Item href="#action5">
                Goerli Testnet
              </NavDropdown.Item>
            </NavDropdown>
          </Nav>
          <Form className="d-flex">
            <Form.Control
              type="search"
              placeholder="Search"
              className="me-2"
              aria-label="Search"
            />
            <Button variant="outline-success">Search</Button>
          </Form>
      </Container>
    </Navbar>
    <Container>
    <Card style={{ height: '5rem',border: 'none', background: 'transparent'}}></Card>
    <Row>
    <Card style={{ width: '14rem',color: 'white',background: 'transparent',opacity: 0.7,alignItems: 'center' }}></Card>
    <Card style={{ width: '12rem',color: 'white',background: 'transparent',opacity: 0.7,alignItems: 'center' }}></Card>
    <Card style={{ width: '14rem',color: 'white',background: 'black',opacity: 0.8,alignItems: 'center',border: '1px solid white' }}>
      <Card.Img variant="top" src={process.env.PUBLIC_URL + '/logo1.png'} style={{height:'9rem', width: '9rem'}} />
      <Card.Body>
        <Card.Title style={{color:'gray'}} >ETHER PRICE</Card.Title>
        <Card.Text>
          ${ethPrice}
        </Card.Text>
          {ethBtc} BTC
      </Card.Body>
    </Card>  
    <Card style={{ width: '3rem',color: 'white',background: 'transparent',opacity: 0.8,alignItems: 'center' }}></Card>
    <Card style={{ width: '14rem',color: 'white',background: 'black',opacity: 0.8,alignItems: 'center',border: '1px solid white' }}>
      <Card.Img variant="top" src={process.env.PUBLIC_URL + '/logo2.png'} style={{height:'9rem', width: '9rem'}} />
      <Card.Body>
        <Card.Title style={{color:'gray'}}>Total Number of Nodes</Card.Title>
        <Card.Text>
          {totalNodes}
        </Card.Text>
      </Card.Body>
    </Card> 
    </Row>

    <Card style={{ height: '4rem', border: 'none', background: 'transparent'}}></Card>
        <Row>
        <Col>
            <Card border="dark" style={{ width: '40rem',height: '40rem',background: 'black',opacity: 0.8,color: 'white',border: '1px solid white'}}>
            <Card.Header style={{fontWeight: 'bold'}}>Header</Card.Header>
            <Card.Body>
            
            </Card.Body>
            <ListGroup className="list-group-flush">
        <ListGroup.Item>Cras justo odio</ListGroup.Item>
        <ListGroup.Item>Dapibus ac facilisis in</ListGroup.Item>
        <ListGroup.Item>Vestibulum at eros</ListGroup.Item>
      </ListGroup>
            </Card>
        </Col>
            <Col>
                <Card border="dark" style={{ width: '38rem',height: '40rem',background: 'black',opacity: 0.8,border: '1px solid white',color:'white' }}>
                <Card.Header style={{fontWeight: 'bold'}}>Header</Card.Header>
                <Card.Body>
                <Card.Title>Dark Card Title</Card.Title>
                <Card.Text>
                    Some quick example text to build on the card title and make up the
                    bulk of the card's content.
                </Card.Text>
                </Card.Body>
                </Card>
        </Col>
      </Row>
      </Container>
      <Card style={{ height: '10rem', border: 'none', background: 'transparent'}}></Card>

    </div>
  );
  } 


export default Navigation;
