import { useState } from 'react';
import axios from 'axios';
import './App.css'; 



function App() {
  const [blockNumber, setBlockNumber] = useState();
  const [giftMessage, setGiftMessage] = useState('');

  async function getRes(){
    try{
      const res = await axios.get('http://localhost:3001/gift').then(res=>{
      const r = res.data;
      setGiftMessage(r);
    })
    }
    catch(e){
      console.log(e);
    }
  }

  return (
    <div className="centered-form">
      <form>
        <input
          type="text"
          placeholder="Enter Block Number"
          value={blockNumber}
        />
        <button type="button" onClick={getRes}>
          Click Me!!!
        </button>
      </form>
      {giftMessage && <p>{giftMessage}</p>}
    </div>
  );

}

export default App;
