import 'bootstrap/dist/css/bootstrap.min.css';
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import Card from 'react-bootstrap/Card';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import ListGroup from 'react-bootstrap/ListGroup';
import NavDropdown from 'react-bootstrap/NavDropdown';
import { useState,useEffect,useContext } from 'react';
import axios from 'axios';
import Style from "./index.module.css";
//import { EtherProvider} from './Etherscan';
import {Link} from "react-router-dom";
const { Alchemy, Network } = require("alchemy-sdk");

import './App.css'

const setting = {
  apiKey: "1dFCMwz6qW-UR-dDdjd5km0vL3aKzjUN",
  Network: Network.ETH_MAINNET,
};


const provider = new Alchemy(setting);

function Navigation() {
    const myStyle = {
      backgroundImage: `url(/back.jpg)`,
      backgroundSize: 'cover',
      opacity: 1.4,
      backgroundRepeat: 'no-repeat',}

      const [ethPrice,setEthprice] = useState('');
      const [ethBtc,setEthbtc] = useState('');
      const [totalNodes,setTotalnodes] = useState('');
      const [transaction,setTransaction] = useState([]);
      const [blockNumber, setBlockNumber] = useState({ num: '' });
  const [hash, setHash] = useState('');
  const [parentHash, setParenthash] = useState('');
  const [nonce, setNonce] = useState('');
  const [timestamp, setTimestamp] = useState('');
  const [difficulty, setDifficulty] = useState('');
  const [number, setNumber] = useState('');
  const [extraData, setExtradata] = useState('');
  const [transactions, setTransactions] = useState('');

      

    useEffect(() => {
      async function getBlockNumber() {
        const response = await axios.get(
          'https://api.etherscan.io/api?module=stats&action=ethprice&apikey=53YRRR2ZWJ1D14S2AU85A8M3IJXX9EKH48'
        );
        const res = await axios.get(
          'https://api.etherscan.io/api?module=stats&action=nodecount&apikey=53YRRR2ZWJ1D14S2AU85A8M3IJXX9EKH48')

      let tenBlock = [];
      try{
