import { useState } from 'react';
import axios from 'axios';




function App() {
  const [blockNumber, setBlockNumber] = useState();
  const [giftMessage, setGiftMessage] = useState('');

  async function getRes(){
    try{
      const res = await axios.post('http://localhost:3001/gift', {
        num: blockNumber
      })
      const r = res.data;
      setGiftMessage(r);
    }
    catch(e){
      console.log(e);
    }
  }

  return (
    <div>
      <h1>Check Block</h1>
        <div>
          <form>
            <input
             type = "text"
             placeholder='Enter Block Number'
             value={blockNumber}
             />
             <button type="submit" onClic></button>
          </form>
        </div>
    </div>
  );

}

export default App;
