import { useState } from 'react';
import axios from 'axios';
import './App.css'; 



function App() {
  const [blockNumber, setBlockNumber] = useState({ num: '' });
  const [hash, setHash] = useState('');
  const [parentHash, setParenthash] = useState('');
  const [nonce, setNonce] = useState('');
  const [timestamp, setTimestamp] = useState('');
  const [difficulty, setDifficulty] = useState('');
  const [number, setNumber] = useState('');
  //const [gasUsed, setgasUsed] = useState('');
  const [miner, setMiner] = useState('');
  const [extraData, setExtradata] = useState('');
  //const [transactions, setTransactions] = useState('');
  //const [baseFee, setBasefee] = useState('');

  function handle(e){
    setBlockNumber({ name: e.target.value });
  }


  async function getRes(){
    try{
      const res = await axios.get('http://localhost:3001/gift', {
        num: blockNumber.name
      })
      const r = res.data;
      setHash(r.hash);
      setParenthash(r.parentHash);
      setTimestamp(r.timestamp);
      setNonce(r.nonce);
      setNumber(r.number);
      setDifficulty(r.difficulty);
      setExtradata(r.extraData)
      setMiner(r.miner);
     // setTransactions(r.transactions);
      //setBasefee(r.baseFeePerGas);
    }
    catch(e){
      console.log(e);
    }
  }

  return (
    <div className="centered-form">
      <form>
        <input
          type="text"
          placeholder="Enter Block Number"
          value={blockNumber.name}
          onChange={handle}
        />
        <button type="button" onClick={getRes}>
          Click Me!!!
        </button>
      </form>
      <div class="block-info">
        {hash && <p>Hash:{hash}</p>}
        {parentHash && <p>ParentHash:{parentHash}</p>}
        {nonce && <p>Nonce:{nonce}</p>}
        {timestamp && <p>TimeStamp:{timestamp}</p>}
        {miner && <p>Miner:{miner}</p>}
        {number && <p>{number}</p>}
        {difficulty && <p>Difficulty:{difficulty}</p>}
        {extraData && <p>extraData:{extraData}</p>}

      </div>
    </div>
  );

}

export default App;
